//******************************************************//*		J P E G  R E A D E R//******************************************************import java.awt.*;import java.awt.image.*;import java.io.*;class JPEGReader extends ImageReader{	public JPEGReader()	{		super();				return;			}// init	//******************************************************************	//*			G E T  I M A G E	//******************************************************************	Image	getImage(String input_directory, String filename, int slice) throws Exception	{		Image	img = null;				Settings.input_file_type = ImageReader.GRAY_8_BIT_IMAGE;							img = getSlice(input_directory, filename, slice);											return(img);									}// end of getImage()	//******************************************************************************	//*		G E T  S L I C E	//******************************************************************************	Image	getSlice(String input_directory, String filename, int slice) throws Exception	{		Image	img = null;		int		image_height = -1, image_width = -1;						if (slice > 1)			throw new Exception("Cannot open jpeg stacks!");				img = Toolkit.getDefaultToolkit().getImage(Settings.input_directory + filename);		do		{			image_height = img.getHeight(null);			image_width = img.getWidth(null);		}		while (image_height == -1 || image_width == -1);				Settings.image_height = image_height;		Settings.image_width = image_width;		Settings.input_file_format = JPEG_FILE;				return(img);			}// end of getSlice()}// end of class JPEGReader